import { Component, OnInit } from '@angular/core';
import { Recipe } from 'src/app/models/recipe';

@Component({
  selector: 'app-recipe-list',
  templateUrl: './recipe-list.component.html',
  styleUrls: ['./recipe-list.component.css']
})
export class RecipeListComponent implements OnInit {
  recipes: Recipe[] = [
    new Recipe('a Test Recipe', 'This is simply a test', 'https://www.acouplecooks.com/wp-content/uploads/2020/12/Honey-Garlic-Shrimp-026.jpg'),
    new Recipe('another Test Recipe', 'This is simply another test', 'https://www.acouplecooks.com/wp-content/uploads/2020/11/Pizza-Soup-006.jpg')
  ];

  constructor() {}

  ngOnInit(): void {}
}
<div class="row">
    <div class="col xs-12">
        <button class="btn btn-success">New recipe</button>
    </div>
</div>
<hr>
<div class="row">
    <div class="col-xs-12">
        <a href="#" class="list-group-item clearfix bg-light my-1 p-3 d-flex justify-content-between" *ngFor="let r of recipes">
            <div class="pull-left">
                <h4 class="list-group-item-heading">{{r.name}}</h4>
                <p class="list-group-item-text">{{r.description}}</p>
            </div>
            <span class="pull-right">
                <img src={{r.imagePath}} alt="" class="img-responsive" style="max-height: 50px;">
            </span>
        </a>

        <!-- <app-recipe-item></app-recipe-item> -->
    </div>
</div>
